CREATE FUNCTION f_select_lock_and_insert( _lockKey varchar, OUT id integer, OUT lock character varying(255), OUT created timestamp without time zone, OUT updated timestamp without time zone, OUT expires character varying(255) ) AS $func$ #variable_conflict use_column BEGIN LOOP SELECT sdl.id, sdl.lock, sdl.created, sdl.updated, sdl.expires FROM skills_db_locks sdl WHERE sdl.lock = _lockKey FOR UPDATE INTO id, lock, created, updated, expires;
EXIT WHEN FOUND;
INSERT INTO skills_db_locks AS l (lock, expires) VALUES (_lockKey, 'true') ON CONFLICT (lock) DO NOTHING RETURNING l.id, l.lock, l.created, l.updated, l.expires INTO id, lock, created, updated, expires;
EXIT WHEN FOUND; END LOOP; END; $func$ LANGUAGE plpgsql;

update skill_definition sd
set enabled = 'true'
where ((sd.type = 'Skill' and sd.group_id is not null) OR sd.type = 'SkillsGroup') and sd.enabled = 'false';

UPDATE level_definition l SET points_from=null, points_to=null from project_definition p, settings settings where p.id = l.project_ref_id and l.skill_ref_id is null and p.project_id != 'Inception' and p.project_id = settings.project_id and settings.setting = 'level.points.enabled' and settings.value = 'false';
UPDATE level_definition l SET points_from=null, points_to=null from skill_definition s, settings settings where s.id = l.skill_ref_id and s.project_id != 'Inception' and s.project_id = settings.project_id and settings.setting = 'level.points.enabled' and settings.value = 'false';
delete from project_error where id in ( select error.id from project_error error, settings settings where error.error_type in ('UnachievableProjectLevel', 'UnachievableSubjectLevel') and error.project_id = settings.project_id and settings.setting = 'level.points.enabled' and settings.value = 'false' );
